import React, { useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const AddCourseDetail = () => {
  const { id } = useParams();
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [videoUrl, setVideoUrl] = useState("");
  const [pdf, setPdf] = useState(null);
  const [quiz, setQuiz] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("videoUrl", videoUrl);
    formData.append("pdf", pdf);
    formData.append("quiz", quiz);

    await axios.post(`http://localhost:5000/api/courses/add-module/${id}`, formData);

    // Reset form after submission
    setTitle("");
    setDescription("");
    setVideoUrl("");
    setPdf(null);
    setQuiz(null);
  };

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <h1 className="text-3xl font-bold text-blue-700 mb-4">Add Module</h1>

      <form
        onSubmit={handleSubmit}
        className="bg-white p-6 rounded shadow-md max-w-lg"
      >
        <input
          type="text"
          placeholder="Module Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="border p-2 w-full mb-3 rounded"
          required
        />
        <textarea
          placeholder="Module Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="border p-2 w-full mb-3 rounded"
          required
        ></textarea>
        <input
          type="text"
          placeholder="Video URL"
          value={videoUrl}
          onChange={(e) => setVideoUrl(e.target.value)}
          className="border p-2 w-full mb-3 rounded"
          required
        />
        <input
          type="file"
          onChange={(e) => setPdf(e.target.files[0])}
          className="border p-2 w-full mb-3 rounded"
          accept="application/pdf"
          required
        />
        <input
          type="file"
          onChange={(e) => setQuiz(e.target.files[0])}
          className="border p-2 w-full mb-3 rounded"
          required
        />
        <button
          type="submit"
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          Add Module
        </button>
      </form>
    </div>
  );
};

export default AddCourseDetail;
