import React from "react";
import {  Routes, Route, Navigate } from "react-router-dom";
import AddCourse from "./pages/AddCourse";
import AddCourseModules from "./pages/AddCourseModules";

// Protect private routes by checking token
const ProtectedRoute = ({ children }) => {
  const token = localStorage.getItem('token');
  if (!token) {
    return <Navigate to="/login" replace />;
  }
  return children;
};

function AdminApp() {
  return (
    <>
      <Routes>
        {/* Redirect root to addcourse */}
        <Route path="/" element={<Navigate to="/admin/addcourse" replace />} />

        <Route path="/admin/addcourse" element={<AddCourse />} />
        <Route path="/admin/coursemodules/:courseId" element={<AddCourseModules />} />

        {/* Catch all - redirect */}
        <Route path="*" element={<Navigate to="/admin/addcourse" replace />} />
      </Routes>
    </>
  );
}

export default AdminApp;
