import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function AddCourseDetail() {
  const { id } = useParams();
  const [moduleData, setModuleData] = useState({
    title: "",
    description: "",
    videoUrl: "",
    pdf: null,
    quiz: null
  });
  const [course, setCourse] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/courses/${id}`)
      .then(res => setCourse(res.data))
      .catch(err => console.error(err));
  }, [id]);

  const handleChange = (e) => {
    if (e.target.type === "file") {
      setModuleData({ ...moduleData, [e.target.name]: e.target.files[0] });
    } else {
      setModuleData({ ...moduleData, [e.target.name]: e.target.value });
    }
  };

  const handleAddModule = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append("title", moduleData.title);
    data.append("description", moduleData.description);
    data.append("videoUrl", moduleData.videoUrl);
    if (moduleData.pdf) data.append("pdf", moduleData.pdf);
    if (moduleData.quiz) data.append("quiz", moduleData.quiz);

    try {
      const res = await axios.post(
        `http://localhost:5000/api/courses/${id}/modules`,
        data,
        { headers: { "Content-Type": "multipart/form-data" } }
      );
      setCourse(res.data);
      setModuleData({ title: "", description: "", videoUrl: "", pdf: null, quiz: null });
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white shadow-lg rounded-xl mt-10">
      {course && (
        <>
          <h1 className="text-2xl font-bold mb-6 text-blue-600">
            Add Modules to: {course.title}
          </h1>

          <form onSubmit={handleAddModule} className="space-y-4">
            <input
              type="text"
              name="title"
              placeholder="Module Title"
              value={moduleData.title}
              onChange={handleChange}
              className="w-full p-2 border rounded"
              required
            />
            <textarea
              name="description"
              placeholder="Module Description"
              value={moduleData.description}
              onChange={handleChange}
              className="w-full p-2 border rounded"
              required
            />
            <input
              type="url"
              name="videoUrl"
              placeholder="Video URL"
              value={moduleData.videoUrl}
              onChange={handleChange}
              className="w-full p-2 border rounded"
            />
            <input
              type="file"
              name="pdf"
              accept="application/pdf"
              onChange={handleChange}
            />
            <input
              type="file"
              name="quiz"
              accept=".pdf,.docx"
              onChange={handleChange}
            />
            <button
              type="submit"
              className="bg-green-500 hover:bg-green-600 text-white py-2 px-4 rounded-lg"
            >
              Add Module
            </button>
          </form>

          <h2 className="text-xl font-semibold mt-6">Modules Added</h2>
          <ul className="mt-2 space-y-2">
            {course.modules.map((mod, idx) => (
              <li key={idx} className="p-3 border rounded bg-gray-50">
                <p className="font-semibold">{mod.title}</p>
                <p className="text-sm text-gray-600">{mod.description}</p>
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}
