import React from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import AddCourse from "./pages/AddCourse";
import AddCourseModules from "./pages/AddCourseModules";
import AdminLogin from "./pages/AdminLogin"; // Adjust path if needed

// Auth check - token presence in localStorage
const isAuthenticated = () => {
  const token = localStorage.getItem("token");
  // Optional: console.log("Checking auth, token:", token);
  return !!token;
};

// Private route wrapper component
function PrivateRoute({ children }) {
  return isAuthenticated() ? children : <Navigate to="/login" replace />;
}

function AdminApp() {
  return (
    <BrowserRouter>
      <Routes>
        {/* Public Login route */}
        <Route path="/login" element={<AdminLogin />} />

        {/* Protected admin routes */}
        <Route
          path="/admin/addcourse"
          element={
            <PrivateRoute>
              <AddCourse />
            </PrivateRoute>
          }
        />
        <Route
          path="/admin/coursemodules/:courseId"
          element={
            <PrivateRoute>
              <AddCourseModules />
            </PrivateRoute>
          }
        />

        {/* Root path redirects based on auth */}
        <Route
          path="/"
          element={
            isAuthenticated() ? (
              <Navigate to="/admin/addcourse" replace />
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />

        {/* Catch-all redirect */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </BrowserRouter>
  );
}

export default AdminApp;
