import React, { useState, useEffect } from "react";
import axios from "axios";

const AddCourse = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState(null);
  const [courses, setCourses] = useState([]);

  // Fetch courses
  useEffect(() => {
    axios.get("http://localhost:5000/api/courses")
      .then(res => setCourses(res.data))
      .catch(err => console.error(err));
  }, []);

  // Handle add course
  const handleAddCourse = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    if (image) formData.append("image", image);

    try {
      await axios.post("http://localhost:5000/api/courses", formData, {
        headers: { "Content-Type": "multipart/form-data" }
      });
      setTitle("");
      setDescription("");
      setImage(null);
      // Refresh course list
      const res = await axios.get("http://localhost:5000/api/courses");
      setCourses(res.data);
    } catch (error) {
      console.error(error);
    }
  };

  // Handle delete
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/courses/${id}`);
      setCourses(courses.filter(course => course._id !== id));
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Add Course</h1>

      {/* Add Course Form */}
      <form onSubmit={handleAddCourse} className="bg-white p-4 rounded shadow-md space-y-3">
        <input
          type="text"
          placeholder="Course Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="border p-2 w-full"
          required
        />
        <textarea
          placeholder="Course Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="border p-2 w-full"
          required
        ></textarea>
        <input
          type="file"
          onChange={(e) => setImage(e.target.files[0])}
          className="border p-2 w-full"
        />
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-800">
          Add Course
        </button>
      </form>

      {/* Course List */}
      <div className="mt-6 grid grid-cols-1 md:grid-cols-3 gap-4">
        {courses.map(course => (
          <div key={course._id} className="bg-white shadow rounded overflow-hidden">
            {course.imageUrl && (
              <img src={`http://localhost:5000/${course.imageUrl}`} alt={course.title} className="w-full h-40 object-cover" />
            )}
            <div className="p-4">
              <h2 className="text-lg font-bold">{course.title}</h2>
              <p className="text-sm text-gray-600">{course.description}</p>
              <div className="flex gap-2 mt-3">
                <button className="bg-green-600 text-white px-3 py-1 rounded">Update</button>
                <button
                  onClick={() => handleDelete(course._id)}
                  className="bg-red-600 text-white px-3 py-1 rounded"
                >
                  Delete
                </button>
                <a
                  href={`/addcoursedetail/${course._id}`}
                  className="bg-purple-600 text-white px-3 py-1 rounded"
                >
                  Add Modules
                </a>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AddCourse;
