import React from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import AddCourse from "./pages/AddCourse";
import AddCourseModules from "./pages/AddCourseModules";
import Login from "./pages/Login";

// Simulate auth check (replace with real auth logic)
const isAuthenticated = () => {
  return !!localStorage.getItem("token"); // e.g. JWT token stored in localStorage
};

// Private route wrapper
function PrivateRoute({ children }) {
  return isAuthenticated() ? children : <Navigate to="/login" replace />;
}

function AdminApp() {
  return (
    <BrowserRouter>
      <Routes>
        {/* Login route */}
        <Route path="/login" element={<Login />} />

        {/* Protected admin routes */}
        <Route
          path="/admin/addcourse"
          element={
            <PrivateRoute>
              <AddCourse />
            </PrivateRoute>
          }
        />
        <Route
          path="/admin/coursemodules/:courseId"
          element={
            <PrivateRoute>
              <AddCourseModules />
            </PrivateRoute>
          }
        />

        {/* Redirect root to login or addcourse based on auth */}
        <Route
          path="/"
          element={
            isAuthenticated() ? (
              <Navigate to="/admin/addcourse" replace />
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />

        {/* Catch all - redirect to root */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </BrowserRouter>
  );
}

export default AdminApp;
