// File: /admin/AddCourseDetail.jsx

import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const AddCourseDetail = () => {
  const { id } = useParams();
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [videoUrl, setVideoUrl] = useState('');
  const [pdf, setPdf] = useState(null);
  const [quiz, setQuiz] = useState(null);
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('title', title);
    formData.append('description', description);
    formData.append('videoUrl', videoUrl);
    formData.append('pdf', pdf);
    formData.append('quiz', quiz);

    try {
      await axios.post(`http://localhost:5000/api/courses/${id}/modules`, formData);
      setMessage('Module added successfully');
      setTitle('');
      setDescription('');
      setVideoUrl('');
      setPdf(null);
      setQuiz(null);
    } catch (err) {
      console.error(err);
      setMessage('Failed to add module');
    }
  };

  return (
    <div className="p-6 bg-white min-h-screen">
      <h1 className="text-2xl font-bold mb-4 text-green-700">Add Module to Course</h1>

      {message && <p className="mb-4 text-blue-600 font-semibold">{message}</p>}

      <form onSubmit={handleSubmit} className="space-y-4 max-w-xl">
        <input
          type="text"
          placeholder="Module Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="border border-gray-300 rounded w-full p-2"
          required
        />

        <textarea
          placeholder="Module Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="border border-gray-300 rounded w-full p-2"
          required
        ></textarea>

        <input
          type="text"
          placeholder="Video URL"
          value={videoUrl}
          onChange={(e) => setVideoUrl(e.target.value)}
          className="border border-gray-300 rounded w-full p-2"
        />

        <div className="flex flex-col space-y-2">
          <label className="text-sm text-gray-700">Upload PDF:</label>
          <input
            type="file"
            accept="application/pdf"
            onChange={(e) => setPdf(e.target.files[0])}
            className="file-input"
          />
        </div>

        <div className="flex flex-col space-y-2">
          <label className="text-sm text-gray-700">Upload Quiz:</label>
          <input
            type="file"
            accept="application/pdf"
            onChange={(e) => setQuiz(e.target.files[0])}
            className="file-input"
          />
        </div>

        <button
          type="submit"
          className="bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700"
        >
          Add Module
        </button>
      </form>
    </div>
  );
};

export default AddCourseDetail;
