import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddCourse = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState(null);
  const [courses, setCourses] = useState([]);
  const navigate = useNavigate();

  // Fetch all courses
  const fetchCourses = async () => {
    const res = await axios.get("http://localhost:5000/api/courses");
    setCourses(res.data);
  };

  useEffect(() => {
    fetchCourses();
  }, []);

  // Add new course
  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("image", image);

    const res = await axios.post("http://localhost:5000/api/courses/add", formData);
    setTitle("");
    setDescription("");
    setImage(null);
    fetchCourses();

    navigate(`/addcoursedetail/${res.data._id}`);
  };

  // Delete course
  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:5000/api/courses/${id}`);
    fetchCourses();
  };

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <h1 className="text-3xl font-bold text-blue-700 mb-4">Add Course</h1>

      {/* Form */}
      <form
        onSubmit={handleSubmit}
        className="bg-white p-6 rounded shadow-md max-w-lg"
      >
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="border p-2 w-full mb-3 rounded"
          required
        />
        <textarea
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="border p-2 w-full mb-3 rounded"
          required
        ></textarea>
        <input
          type="file"
          onChange={(e) => setImage(e.target.files[0])}
          className="border p-2 w-full mb-3 rounded"
          accept="image/*"
          required
        />
        <button
          type="submit"
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          Add Course
        </button>
      </form>

      {/* Display Courses */}
      <h2 className="text-2xl font-bold mt-8">Courses List</h2>
      <div className="grid md:grid-cols-3 gap-4 mt-4">
        {courses.map((course) => (
          <div key={course._id} className="bg-white shadow rounded p-3">
            <img
              src={`http://localhost:5000${course.image}`}
              alt={course.title}
              className="w-full h-40 object-cover rounded"
            />
            <h3 className="text-lg font-semibold mt-2">{course.title}</h3>
            <p className="text-gray-600">{course.description}</p>
            <div className="flex gap-2 mt-2">
              <button
                onClick={() => navigate(`/addcoursedetail/${course._id}`)}
                className="bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600"
              >
                Add Module
              </button>
              <button
                onClick={() => handleDelete(course._id)}
                className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AddCourse;
