import React from "react";
import { BrowserRouter, Routes, Route, Navigate, useNavigate } from "react-router-dom";
import AddCourse from "./pages/AddCourse";
import AddCourseModules from "./pages/AddCourseModules";

// Simple dummy login page for testing
function Login() {
  const navigate = useNavigate();

  const handleLogin = () => {
    // Fake login, set token
    localStorage.setItem("token", "dummy-token");
    // Redirect to addcourse after login
    navigate("/admin/addcourse");
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Login Page</h2>
      <button onClick={handleLogin}>Login (set token)</button>
    </div>
  );
}

const isAuthenticated = () => {
  return !!localStorage.getItem("token");
};

function PrivateRoute({ children }) {
  return isAuthenticated() ? children : <Navigate to="/login" replace />;
}

function AdminApp() {
  return (
    <BrowserRouter>
      <Routes>
        <Route path="/login" element={<Login />} />

        <Route
          path="/admin/addcourse"
          element={
            <PrivateRoute>
              <AddCourse />
            </PrivateRoute>
          }
        />
        <Route
          path="/admin/coursemodules/:courseId"
          element={
            <PrivateRoute>
              <AddCourseModules />
            </PrivateRoute>
          }
        />

        <Route
          path="/"
          element={
            isAuthenticated() ? (
              <Navigate to="/admin/addcourse" replace />
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />

        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </BrowserRouter>
  );
}

export default AdminApp;
