import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddCourse = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState(null);
  const [courses, setCourses] = useState([]);
  const navigate = useNavigate();

  // Fetch existing courses
  const fetchCourses = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/courses");
      setCourses(res.data);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchCourses();
  }, []);

  // Add new course
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!title || !description || !image) {
      alert("Please fill all fields");
      return;
    }
    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("image", image);

    try {
      await axios.post("http://localhost:5000/api/courses", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setTitle("");
      setDescription("");
      setImage(null);
      fetchCourses();

      // Redirect removed as requested
      // navigate(`/admin/coursemodules/${res.data._id}`);
    } catch (error) {
      console.error(error);
      alert("Error saving course");
    }
  };

  // Delete course
  const handleDelete = async (id) => {
    if (!window.confirm("Delete this course?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/courses/${id}`);
      setCourses(courses.filter((c) => c._id !== id));
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">Add Course</h1>
      <form onSubmit={handleSubmit} className="space-y-4 bg-white p-6 rounded shadow">
        <input
          type="text"
          placeholder="Course Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="border rounded p-2 w-full"
          required
        />
        <textarea
          placeholder="Course Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="border rounded p-2 w-full"
          rows={4}
          required
        />
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setImage(e.target.files[0])}
          className="border rounded p-2 w-full"
          required
        />
        <button
          type="submit"
          className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
        >
          Add Course
        </button>
      </form>

      <h2 className="text-2xl font-semibold mt-10 mb-4">Courses</h2>
<div className="grid gap-8 sm:grid-cols-1 md:grid-cols-2 lg:grid-cols-2 xl:grid-cols-3 px-4 sm:px-6 md:px-8">
  {courses.map((course) => (
    <div
      key={course._id}
      className="bg-white shadow-lg rounded-lg overflow-hidden transition-transform duration-300 hover:scale-105 hover:shadow-2xl max-w-xl mx-auto"
    >
      <img
        src={`http://localhost:5000${course.imageUrl}`}
        alt={course.title}
        className="w-full h-48 object-cover rounded-t-lg"
      />
      <div className="p-6">
        <h3 className="font-extrabold text-xl text-blue-700">{course.title}</h3>
        <p className="text-gray-700 mt-3 text-base line-clamp-3">{course.description}</p>
        <div className="flex flex-wrap gap-4 mt-6">
          <button className="bg-green-600 hover:bg-green-700 transition-colors text-white px-5 py-2 rounded-md shadow-md font-semibold min-w-[100px] text-center">
            Update
          </button>
          <button
            onClick={() => handleDelete(course._id)}
            className="bg-red-600 hover:bg-red-700 transition-colors text-white px-5 py-2 rounded-md shadow-md font-semibold min-w-[100px] text-center"
          >
            Delete
          </button>
          <button
            onClick={() => navigate(`/admin/coursemodules/${course._id}`)}
            className="bg-purple-600 hover:bg-purple-700 transition-colors text-white px-5 py-2 rounded-md shadow-md font-semibold min-w-[100px] text-center"
          >
            Add Modules
          </button>
        </div>
      </div>
    </div>
  ))}
</div>


    </div>
  );
};

export default AddCourse;
