import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const AddCourseDetail = () => {
  const { courseId } = useParams();

  // Module form fields
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [videoUrl, setVideoUrl] = useState("");
  const [pdfFile, setPdfFile] = useState(null);
  const [quizFile, setQuizFile] = useState(null);

  // Existing modules for this course
  const [modules, setModules] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fetch modules for this course
  const fetchModules = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/courses/${courseId}`);
      setModules(res.data.modules || []);
    } catch (error) {
      console.error("Error fetching modules:", error);
    }
  };

  useEffect(() => {
    fetchModules();
  }, [courseId]);

  // Add new module
  const handleAddModule = async (e) => {
    e.preventDefault();

    if (!title || !description) {
      alert("Please fill in title and description.");
      return;
    }

    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("videoUrl", videoUrl);
    if (pdfFile) formData.append("pdfFile", pdfFile);
    if (quizFile) formData.append("quizFile", quizFile);

    try {
      setLoading(true);
      await axios.post(
        `http://localhost:5000/api/coursedetails/${courseId}/modules`,
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );
      setTitle("");
      setDescription("");
      setVideoUrl("");
      setPdfFile(null);
      setQuizFile(null);
      fetchModules();
    } catch (error) {
      console.error("Error adding module:", error);
    } finally {
      setLoading(false);
    }
  };

  // Delete module
  const handleDeleteModule = async (moduleId) => {
    try {
      await axios.delete(
        `http://localhost:5000/api/coursedetails/${courseId}/modules/${moduleId}`
      );
      fetchModules();
    } catch (error) {
      console.error("Error deleting module:", error);
    }
  };

  // Update module - you can implement edit forms similarly if needed (for now we skip update)

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-6">Add Course Modules</h1>

      {/* Add Module Form */}
      <form
        onSubmit={handleAddModule}
        className="bg-white p-5 rounded shadow space-y-4"
      >
        <input
          type="text"
          placeholder="Module Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="border p-2 w-full rounded"
          required
        />
        <textarea
          placeholder="Module Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="border p-2 w-full rounded"
          rows={3}
          required
        />
        <input
          type="url"
          placeholder="Video URL (embed link)"
          value={videoUrl}
          onChange={(e) => setVideoUrl(e.target.value)}
          className="border p-2 w-full rounded"
        />
        <div>
          <label className="block mb-1 font-semibold">Upload PDF File</label>
          <input
            type="file"
            accept="application/pdf"
            onChange={(e) => setPdfFile(e.target.files[0])}
            className="border p-2 w-full rounded"
          />
        </div>
        <div>
          <label className="block mb-1 font-semibold">Upload Quiz File</label>
          <input
            type="file"
            accept=".pdf,.doc,.docx,.xlsx,.xls,.txt"
            onChange={(e) => setQuizFile(e.target.files[0])}
            className="border p-2 w-full rounded"
          />
        </div>
        <button
          type="submit"
          disabled={loading}
          className="bg-blue-600 text-white px-5 py-2 rounded hover:bg-blue-700"
        >
          {loading ? "Adding..." : "Add Module"}
        </button>
      </form>

      {/* Modules List */}
      <div className="mt-8 space-y-4">
        {modules.length === 0 ? (
          <p className="text-gray-500">No modules added yet.</p>
        ) : (
          modules.map((mod) => (
            <div
              key={mod._id}
              className="bg-white p-4 rounded shadow flex justify-between items-center"
            >
              <div>
                <h3 className="font-semibold">{mod.title}</h3>
                <p className="text-sm text-gray-600">{mod.description}</p>
                {mod.videoUrl && (
                  <p className="text-xs text-blue-500">Video: {mod.videoUrl}</p>
                )}
                {mod.pdfUrl && (
                  <a
                    href={`http://localhost:5000${mod.pdfUrl}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-xs text-indigo-600 underline"
                  >
                    PDF File
                  </a>
                )}
                {mod.quizUrl && (
                  <a
                    href={`http://localhost:5000${mod.quizUrl}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-xs text-green-600 underline ml-2"
                  >
                    Quiz File
                  </a>
                )}
              </div>
              <button
                onClick={() => handleDeleteModule(mod._id)}
                className="bg-red-600 text-white px-3 py-1 rounded"
              >
                Delete
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default AddCourseDetail;
