import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AddCourse = () => {
  const [form, setForm] = useState({ title: '', description: '', image: null });
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'image') {
      setForm({ ...form, image: files[0] });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const imgForm = new FormData();
      imgForm.append('file', form.image);
      const uploadRes = await axios.post('http://localhost:5000/api/upload/file', imgForm);
      const imageUrl = uploadRes.data.url;

      const courseRes = await axios.post('http://localhost:5000/api/courses/add', {
        title: form.title,
        description: form.description,
        image: imageUrl
      });

      const courseId = courseRes.data._id;
      navigate(`/add-course/${courseId}/details`);
    } catch (err) {
      alert('Upload failed');
    }
  };

  return (
    <div className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
      <h2 className="text-2xl font-semibold mb-4">Add New Course</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          name="title"
          placeholder="Course Title"
          onChange={handleChange}
          required
          className="w-full px-4 py-2 border rounded"
        />
        <textarea
          name="description"
          placeholder="Course Description"
          onChange={handleChange}
          required
          className="w-full px-4 py-2 border rounded"
        />
        <input
          type="file"
          name="image"
          accept="image/*"
          onChange={handleChange}
          required
          className="w-full"
        />
        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Next
        </button>
      </form>
    </div>
  );
};

export default AddCourse;
