import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const AddCourseDetail = () => {
  const { id: courseId } = useParams();
  const [module, setModule] = useState({
    title: '',
    description: '',
    pdf: null,
    quiz: null
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (files) {
      setModule({ ...module, [name]: files[0] });
    } else {
      setModule({ ...module, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const pdfForm = new FormData();
      pdfForm.append('file', module.pdf);
      const pdfRes = await axios.post('http://localhost:5000/api/upload/file', pdfForm);
      const pdfUrl = pdfRes.data.url;

      const quizForm = new FormData();
      quizForm.append('file', module.quiz);
      const quizRes = await axios.post('http://localhost:5000/api/upload/file', quizForm);
      const quizUrl = quizRes.data.url;

      await axios.post('http://localhost:5000/api/modules/add', {
        courseId,
        title: module.title,
        description: module.description,
        pdf: pdfUrl,
        quiz: quizUrl
      });

      alert('Module added!');
      setModule({ title: '', description: '', pdf: null, quiz: null });
    } catch (err) {
      alert('Failed to add module');
    }
  };

  return (
    <div className="bg-white shadow-md rounded px-8 pt-6 pb-8">
      <h2 className="text-2xl font-semibold mb-4">Add Module to Course</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          name="title"
          placeholder="Module Title"
          onChange={handleChange}
          required
          className="w-full px-4 py-2 border rounded"
        />
        <textarea
          name="description"
          placeholder="Module Description"
          onChange={handleChange}
          required
          className="w-full px-4 py-2 border rounded"
        />
        <input
          type="file"
          name="pdf"
          accept=".pdf"
          onChange={handleChange}
          required
          className="w-full"
        />
        <input
          type="file"
          name="quiz"
          accept=".pdf,.docx,.txt"
          onChange={handleChange}
          required
          className="w-full"
        />
        <button
          type="submit"
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          Add Module
        </button>
      </form>
    </div>
  );
};

export default AddCourseDetail;
