import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Course = () => {
  const [courses, setCourses] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:5000/api/courses")
      .then((res) => res.json())
      .then((data) => setCourses(data))
      .catch((err) => console.error(err));
  }, []);

  return (
    <div className="p-6 max-w-6xl mx-auto bg-gray-50 min-h-screen">
      <h1 className="text-3xl font-bold mb-8 text-center">Courses</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {courses.map((course) => (
  <div key={course._id} className="bg-white shadow rounded overflow-hidden flex flex-col">
    <img
      src={`http://localhost:5000${course.imageUrl}`}
      alt={course.title}
      className="h-48 object-cover w-full"
    />
    <div className="p-4 flex flex-col flex-grow">
      <h2 className="text-xl font-semibold">{course.title}</h2>
      <p className="text-gray-700 mt-2 flex-grow">{course.description}</p>
      <button
        onClick={() => navigate(`/course/${course._id}`)}
        className="mt-4 bg-blue-600 text-white rounded py-2 hover:bg-blue-700"
      >
        Learn Now
      </button>
    </div>
  </div>
))}

      </div>
    </div>
  );
};

export default Course;
