import { FaLaptopCode, FaMobileAlt } from "react-icons/fa";

const courses = [
  {
    title: "Web Development",
    pcTools: ["VS Code", "Google Chrome DevTools", "Node.js", "Git", "Postman"],
    phoneTools: ["Acode", "Termux", "Spck Editor", "Dcoder", "GitHub App"],
    color: "from-green-500 to-blue-500",
  },
  {
    title: "Graphics Design",
    pcTools: ["Adobe Photoshop", "Adobe Illustrator", "Figma", "CorelDRAW", "Canva"],
    phoneTools: ["Canva", "Pixellab", "Snapseed", "Adobe Express", "Ibis Paint X"],
    color: "from-blue-500 to-green-500",
  },
  {
    title: "Video Editing",
    pcTools: ["Adobe Premiere Pro", "After Effects", "DaVinci Resolve", "Filmora", "Final Cut Pro"],
    phoneTools: ["CapCut", "KineMaster", "VN", "InShot", "Alight Motion"],
    color: "from-green-600 to-blue-600",
  },
];

export default function LearningSection() {
  return (
    <section className="py-16 bg-gradient-to-b from-white to-green-50">
      <h2 className="text-4xl font-bold text-center mb-12 text-green-700">
        How You Can Learn & Practice
      </h2>
      <div className="grid gap-8 md:grid-cols-3 px-6">
        {courses.map((course, idx) => (
          <div
            key={idx}
            className={`p-6 rounded-xl shadow-lg bg-gradient-to-br ${course.color} text-white transform hover:scale-105 transition-all duration-500 ease-in-out delay-[${idx * 100}ms]`}
          >
            <h3 className="text-2xl font-semibold mb-4">{course.title}</h3>

            <div className="flex flex-col gap-4">
              {/* PC Tools */}
              <div>
                <h4 className="flex items-center text-lg font-medium mb-2">
                  <FaLaptopCode className="mr-2 text-yellow-200" /> PC Users
                </h4>
                <ul className="grid grid-cols-1 sm:grid-cols-2 gap-1 text-sm">
                  {course.pcTools.map((tool, i) => (
                    <li key={i} className="bg-white bg-opacity-20 px-2 py-1 rounded">
                      {tool}
                    </li>
                  ))}
                </ul>
              </div>

              {/* Phone Tools */}
              <div>
                <h4 className="flex items-center text-lg font-medium mb-2">
                  <FaMobileAlt className="mr-2 text-yellow-200" /> Phone Users
                </h4>
                <ul className="grid grid-cols-1 sm:grid-cols-2 gap-1 text-sm">
                  {course.phoneTools.map((tool, i) => (
                    <li key={i} className="bg-white bg-opacity-20 px-2 py-1 rounded">
                      {tool}
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
