// client/src/CourseDetail.jsx
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function CourseDetail() {
  const { id } = useParams();
  const [course, setCourse] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5000/api/courses/${id}`)
      .then((res) => res.json())
      .then((data) => setCourse(data))
      .catch((err) => console.error(err));
  }, [id]);

  if (!course) return <p className="text-center mt-10">Loading...</p>;

  return (
    <div className="bg-gray-50 min-h-screen p-6">
      <h1 className="text-3xl font-bold text-green-700 mb-6 text-center">
        {course.title}
      </h1>
      <p className="text-gray-700 text-center mb-10">{course.description}</p>

      <div className="space-y-8">
        {course.modules.map((mod, index) => (
          <div
            key={index}
            className="bg-white p-6 rounded-lg shadow hover:shadow-md transition-shadow"
          >
            <h2 className="text-2xl font-semibold text-blue-700 mb-3">
              {mod.title}
            </h2>
            <p className="text-gray-600 mb-4">{mod.description}</p>

            {/* Video Embed */}
            {mod.videoUrl && (
              <div className="mb-4">
                <iframe
                  src={mod.videoUrl}
                  title={mod.title}
                  className="w-full h-64 rounded-lg"
                  frameBorder="0"
                  allowFullScreen
                ></iframe>
              </div>
            )}

            {/* PDF Download */}
            {mod.pdf && (
              <a
                href={`http://localhost:5000/uploads/pdfs/${mod.pdf}`}
                target="_blank"
                rel="noopener noreferrer"
                className="block mt-2 px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700 w-max"
              >
                üìÑ Download PDF
              </a>
            )}

            {/* Quiz Download */}
            {mod.quiz && (
              <a
                href={`http://localhost:5000/uploads/quizzes/${mod.quiz}`}
                target="_blank"
                rel="noopener noreferrer"
                className="block mt-2 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 w-max"
              >
                üìù Download Quiz
              </a>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default CourseDetail;
