import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const CourseDetail = () => {
  const { id } = useParams();
  const [modules, setModules] = useState([]);
  const [courseTitle, setCourseTitle] = useState("");

  useEffect(() => {
    fetch(`http://localhost:5000/api/courses/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setCourseTitle(data.title);
        setModules(data.modules || []);
      })
      .catch((err) => console.error("Error fetching course details:", err));
  }, [id]);

  return (
    <div className="p-8 bg-gray-50 min-h-screen">
      <h1 className="text-3xl font-bold mb-6">{courseTitle}</h1>
      {modules.map((module, index) => (
        <div
          key={module._id || index}
          className="mb-6 bg-white rounded-lg shadow-lg p-6"
        >
          <h2 className="text-2xl font-semibold">{module.title}</h2>
          <p className="text-gray-700 mt-2">{module.description}</p>

          {module.videoUrl && (
            <div className="mt-4">
              <iframe
                className="w-full h-64 rounded"
                src={module.videoUrl}
                title="Module Video"
                frameBorder="0"
                allowFullScreen
              ></iframe>
            </div>
          )}

          {module.pdfUrl && (
            <a
              href={`http://localhost:5000/${module.pdfUrl}`}
              target="_blank"
              rel="noopener noreferrer"
              className="mt-4 block text-blue-500 underline"
            >
              üìÑ View PDF
            </a>
          )}

          {module.quizUrl && (
            <a
              href={`http://localhost:5000/${module.quizUrl}`}
              target="_blank"
              rel="noopener noreferrer"
              className="mt-2 block text-green-500 underline"
            >
              üìù Take Quiz
            </a>
          )}
        </div>
      ))}
    </div>
  );
};

export default CourseDetail;
