import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';

function Navbar() {
  const location = useLocation();
  const navigate = useNavigate();

  // Handler to navigate all links to /login
  const handleClick = (e) => {
    e.preventDefault();
    navigate('/login');
  };

  return (
    <nav className="bg-green-600 fixed w-full top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16 items-center">
          <Link to="/login" className="text-2xl font-bold text-white">
            Learn<span className="text-indigo-300">X</span>
          </Link>

          <div className="flex space-x-6 items-center">
            <a
              href="/login"
              onClick={handleClick}
              className="text-white font-medium hover:text-green-200 transition"
            >
              Home
            </a>
            <a
              href="/login"
              onClick={handleClick}
              className="text-white font-medium hover:text-green-200 transition"
            >
              How to Learn
            </a>
            <a
              href="/login"
              onClick={handleClick}
              className="bg-white text-green-600 font-medium px-4 py-2 rounded transition hover:bg-green-100"
            >
              Courses
            </a>
          </div>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
