import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const CourseDetail = () => {
  const { id } = useParams();
  const [course, setCourse] = useState(null);
  const [modules, setModules] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/courses/${id}`)
      .then(res => setCourse(res.data));

    axios.get(`http://localhost:5000/api/modules/${id}`)
      .then(res => setModules(res.data));
  }, [id]);

  if (!course) return <p>Loading...</p>;

  return (
    <div>
      <div className="bg-white rounded shadow p-4 mb-6">
        <img
          src={`http://localhost:5000${course.image}`}
          alt={course.title}
          className="w-full h-60 object-cover rounded mb-4"
        />
        <h2 className="text-2xl font-bold mb-2">{course.title}</h2>
        <p className="text-gray-700">{course.description}</p>
      </div>

      <h3 className="text-xl font-semibold mb-4">Modules</h3>
      <div className="space-y-4">
        {modules.map(mod => (
          <div key={mod._id} className="bg-white shadow rounded p-4">
            <h4 className="text-lg font-semibold mb-1">{mod.title}</h4>
            <p className="text-sm text-gray-600 mb-2">{mod.description}</p>
            <div className="flex gap-4">
              <a
                href={`http://localhost:5000${mod.pdf}`}
                target="_blank"
                rel="noreferrer"
                className="text-blue-600 underline"
              >
                View PDF
              </a>
              <a
                href={`http://localhost:5000${mod.quiz}`}
                target="_blank"
                rel="noreferrer"
                className="text-green-600 underline"
              >
                View Quiz
              </a>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CourseDetail;
