import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const CourseDetail = () => {
  const { courseId } = useParams();
  const [course, setCourse] = useState(null);
  const [selectedModuleIndex, setSelectedModuleIndex] = useState(0);

  useEffect(() => {
    fetch(`http://localhost:5000/api/courses/${courseId}`)
      .then((res) => res.json())
      .then((data) => {
        setCourse(data);
      })
      .catch((err) => console.error(err));
  }, [courseId]);

  if (!course) {
    return <p className="p-6 text-center">Loading...</p>;
  }

  const modules = course.modules || [];
  const selectedModule = modules[selectedModuleIndex] || null;

  return (
    <div className="max-w-6xl mx-auto p-6 flex flex-col md:flex-row gap-6 min-h-screen bg-gray-50 ">
      {/* Module List */}
      <div className="md:w-1/3 bg-white p-4 rounded shadow max-h-[80vh] overflow-auto">
        <h2 className="text-xl font-bold mb-4">Modules</h2>
        {modules.length === 0 ? (
          <p className="text-gray-500">No modules available.</p>
        ) : (
          <ul className="space-y-3">
            {modules.map((mod, idx) => (
              <li
                key={mod._id}
                onClick={() => setSelectedModuleIndex(idx)}
                className={`cursor-pointer p-2 rounded ${
                  idx === selectedModuleIndex
                    ? "bg-blue-600 text-white"
                    : "hover:bg-gray-200"
                }`}
              >
                {`Module ${idx + 1}: ${mod.title}`}
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Selected Module Detail */}
      <div className="md:w-2/3 bg-white p-6 rounded shadow overflow-auto max-h-[80vh]">
        {selectedModule ? (
          <>
            <h2 className="text-2xl font-bold mb-3">{selectedModule.title}</h2>
            <p className="mb-4">{selectedModule.description}</p>

            {/* Embedded Video */}
            {selectedModule.videoUrl && (
              <div className="mb-4 aspect-video">
                <iframe
                  src={selectedModule.videoUrl}
                  title={selectedModule.title}
                  frameBorder="0"
                  allowFullScreen
                  className="w-full h-full rounded"
                ></iframe>
              </div>
            )}

            {/* PDF Download */}
            {selectedModule.pdfUrl && (
              <a
                href={`http://localhost:5000${selectedModule.pdfUrl}`}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-block bg-indigo-600 text-white px-4 py-2 rounded mr-3"
              >
                Download PDF
              </a>
            )}

            {/* Quiz Download */}
            {selectedModule.quizUrl && (
              <a
                href={`http://localhost:5000${selectedModule.quizUrl}`}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-block bg-green-600 text-white px-4 py-2 rounded"
              >
                Download Quiz
              </a>
            )}
          </>
        ) : (
          <p className="text-gray-500">Select a module to see details.</p>
        )}
      </div>
    </div>
  );
};

export default CourseDetail;
