import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

// ✅ DESCRIPTION:
// This page displays detailed modules for a course.
// It fetches course data by courseId from backend.

const CourseDetail = () => {
  const { id } = useParams();
  const [course, setCourse] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5000/api/courses/${id}`)
      .then(res => res.json())
      .then(data => setCourse(data))
      .catch(err => console.error(err));
  }, [id]);

  if (!course) {
    return <p className="text-center mt-10">Loading...</p>;
  }

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-4">{course.title}</h1>
      <p className="mb-6 text-gray-700">{course.description}</p>

      {course.modules && course.modules.length > 0 ? (
        course.modules.map((mod, index) => (
          <div key={index} className="bg-white shadow rounded p-4 mb-4">
            <h2 className="text-xl font-semibold">{mod.title}</h2>
            <p className="text-gray-600">{mod.description}</p>

            {mod.videoUrl && (
              <video controls className="w-full mt-3 rounded">
                <source src={mod.videoUrl} type="video/mp4" />
              </video>
            )}

            {mod.pdfUrl && (
              <a
                href={mod.pdfUrl}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-500 mt-2 block"
              >
                View PDF
              </a>
            )}

            {mod.quizLink && (
              <a
                href={mod.quizLink}
                target="_blank"
                rel="noopener noreferrer"
                className="text-green-500 mt-2 block"
              >
                Take Quiz
              </a>
            )}
          </div>
        ))
      ) : (
        <p>No modules found for this course.</p>
      )}
    </div>
  );
};

export default CourseDetail;
