import React from 'react';
import { useNavigate } from 'react-router-dom';

function Navbar() {
  const navigate = useNavigate();

  const handleClick = (e) => {
    e.preventDefault();
    navigate('/login');
  };

  return (
    <nav className="bg-white fixed w-full top-0 z-50 shadow-md">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16 items-center">
          <a
            href="/login"
            onClick={handleClick}
            className="text-2xl font-bold text-green-600 cursor-pointer"
          >
            Learn<span className="text-indigo-600">X</span>
          </a>

          <div className="flex space-x-6 items-center">
            <a
              href="/login"
              onClick={handleClick}
              className="text-green-600 font-medium hover:text-green-800 transition cursor-pointer"
            >
              Home
            </a>
            <a
              href="/login"
              onClick={handleClick}
              className="text-green-600 font-medium hover:text-green-800 transition cursor-pointer"
            >
              How to Learn
            </a>
            <a
              href="/login"
              onClick={handleClick}
              className="bg-green-600 text-white font-medium px-4 py-2 rounded hover:bg-green-700 transition cursor-pointer"
            >
              Courses
            </a>
          </div>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
