import React from "react";

const LearningGuide = () => {
  const guides = [
    {
      titleEn: "Web Development",
      titleAm: "የድር ልማት",
      descEn:
        "You can learn on your phone or PC with step-by-step videos, practice by building small projects, and get feedback.",
      descAm:
        "በስልክዎ ወይም በኮምፒውተርዎ በመምሪያ ቪዲዮዎች መማር ይችላሉ፣ ትንሽ ፕሮጀክቶች በመገንባት በተግባር ይሞክሩ፣ እና አስተያየት ያግኙ።",
    },
    {
      titleEn: "Graphics Design",
      titleAm: "ግራፊክስ ንድፍ",
      descEn:
        "Practice designing posters, logos, and social media banners with free tools available on both mobile and desktop.",
      descAm:
        "ፖስተሮች፣ ሎጎዎች፣ የማህበራዊ ሚዲያ ባነሮች በሞባይልና በኮምፒውተር ላይ የሚገኙ በነፃ መሳሪያዎች በመጠቀም በተግባር ይሞክሩ።",
    },
    {
      titleEn: "Programming",
      titleAm: "ፕሮግራሚንግ",
      descEn:
        "Write code directly on your PC or try online coding platforms from your phone for hands-on practice.",
      descAm:
        "ኮድ በቀጥታ በኮምፒውተርዎ ይጻፉ ወይም በስልክዎ የመስመር ላይ የኮዲንግ መድረኮችን በመጠቀም በተግባር ይሞክሩ።",
    },
  ];

  return (
    <section className="bg-gradient-to-b from-green-50 via-white to-blue-50 py-16 px-4 md:px-16">
      <div className="max-w-6xl mx-auto text-center">
        <h2 className="text-4xl font-bold text-green-700 mb-4 animate-fade-in-down">
          How You Can Learn & Practice
        </h2>
        <p className="text-lg text-blue-800 mb-12 animate-fade-in">
          Learn from anywhere, on any device — in both English & Amharic
        </p>
        <div className="grid md:grid-cols-3 gap-8">
          {guides.map((guide, index) => (
            <div
              key={index}
              className="bg-white shadow-lg rounded-xl p-6 border border-green-100 hover:scale-105 transform transition-all duration-500 ease-in-out hover:shadow-2xl"
              style={{ animationDelay: `${index * 0.2}s` }}
            >
              <h3 className="text-2xl font-semibold text-green-700 mb-2">
                {guide.titleEn}
              </h3>
              <p className="text-sm text-blue-700 mb-4">{guide.descEn}</p>
              <h3 className="text-xl font-semibold text-green-600 mb-1">
                {guide.titleAm}
              </h3>
              <p className="text-sm text-blue-600">{guide.descAm}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default LearningGuide;
