import React from "react";
import { motion } from "framer-motion";

const learningPaths = [
  {
    title: "Web Development",
    softwares: [
      "Visual Studio Code",
      "Google Chrome Developer Tools",
      "Git & GitHub",
      "Node.js",
      "Postman",
    ],
    amharic: "ለድር ልምድ የሚጠቅሙ ሶፍትዌሮች",
    icon: "💻",
  },
  {
    title: "Graphics Design",
    softwares: [
      "Adobe Photoshop",
      "Adobe Illustrator",
      "Figma",
      "Canva",
      "CorelDRAW",
    ],
    amharic: "ለግራፊክስ ዲዛይን የሚጠቅሙ ሶፍትዌሮች",
    icon: "🎨",
  },
  {
    title: "Video Editing",
    softwares: [
      "Adobe Premiere Pro",
      "Adobe After Effects",
      "Filmora",
      "DaVinci Resolve",
      "CapCut",
    ],
    amharic: "ለቪዲዮ ኤዲቲንግ የሚጠቅሙ ሶፍትዌሮች",
    icon: "🎥",
  },
];

const LearningSection = () => {
  return (
    <div className="bg-gradient-to-b from-green-50 via-white to-blue-50 py-16 px-6 md:px-20 mt-20">
      <h2 className="text-3xl md:text-4xl font-bold text-center mb-12 text-green-700">
        📚 How You Can Learn & Practice
      </h2>

      <div className="grid md:grid-cols-3 gap-8">
        {learningPaths.map((path, index) => (
          <motion.div
            key={path.title}
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{
              duration: 0.6,
              delay: index * 0.2,
              ease: "easeOut",
            }}
            className="bg-white shadow-lg rounded-xl p-6 hover:shadow-2xl transform hover:-translate-y-2 transition-all duration-300 border-t-4 border-green-500"
          >
            <div className="text-5xl text-center mb-4">{path.icon}</div>
            <h3 className="text-xl font-semibold text-center text-blue-700 mb-4">
              {path.title}
            </h3>

            <ul className="list-disc pl-6 mb-4 text-gray-700">
              {path.softwares.map((software, i) => (
                <li key={i}>{software}</li>
              ))}
            </ul>

            <p className="text-green-700 font-medium text-center">
              {path.amharic}
            </p>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default LearningSection;
