import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

// ✅ DESCRIPTION:
// This page fetches all courses from the backend and displays them in a grid.
// Tailwind classes are used for styling.

const Courses = () => {
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/courses")
      .then(res => res.json())
      .then(data => setCourses(data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <h1 className="text-3xl font-bold text-center mb-8">Available Courses</h1>
      <div className="grid gap-6 md:grid-cols-3 sm:grid-cols-2 grid-cols-1">
        {courses.map(course => (
          <div key={course._id} className="bg-white shadow-lg rounded-lg overflow-hidden">
            <img src={course.imageUrl} alt={course.title} className="w-full h-48 object-cover" />
            <div className="p-4">
              <h2 className="text-lg font-bold">{course.title}</h2>
              <p className="text-gray-600">{course.description}</p>
              <Link
                to={`/course/${course._id}`}
                className="mt-3 inline-block bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
              >
                View Course
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Courses;
