import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function CourseDetail() {
  const { id } = useParams();
  const [modules, setModules] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:5000/api/courses/${id}/modules`)
      .then((res) => res.json())
      .then((data) => setModules(data))
      .catch((err) => console.error("Error fetching modules:", err));
  }, [id]);

  return (
    <div className="max-w-5xl mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">Course Modules</h1>

      {modules.length === 0 ? (
        <p className="text-gray-500">No modules found for this course.</p>
      ) : (
        <div className="space-y-6">
          {modules.map((mod) => (
            <div
              key={mod._id}
              className="bg-white p-5 rounded-lg shadow hover:shadow-lg transition"
            >
              <h2 className="text-xl font-semibold text-gray-800">{mod.title}</h2>
              <p className="text-gray-600 mt-1">{mod.description}</p>

              {mod.videoUrl && (
                <div className="mt-4">
                  <iframe
                    src={mod.videoUrl}
                    title={mod.title}
                    className="w-full h-64 rounded-lg"
                    allowFullScreen
                  ></iframe>
                </div>
              )}

              <div className="mt-4 flex gap-4">
                {mod.pdfFile && (
                  <a
                    href={`http://localhost:5000/uploads/pdfs/${mod.pdfFile}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-blue-500 underline"
                  >
                    View PDF
                  </a>
                )}
                {mod.quizFile && (
                  <a
                    href={`http://localhost:5000/uploads/quizzes/${mod.quizFile}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-green-500 underline"
                  >
                    Download Quiz
                  </a>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
